// Extrair pergunta do input
const pergunta = ($input.item.json.pergunta || $input.item.json.body?.pergunta || $input.item.json.query?.pergunta || 'hoje').toLowerCase();

// Detectar per√≠odo
let periodo = 'hoje';

if (pergunta.includes('ontem')) {
  periodo = 'ontem';
} else if (pergunta.includes('anteontem')) {
  periodo = 'anteontem';
} else if (pergunta.includes('semana')) {
  periodo = 'esta semana';
} else if (pergunta.includes('m√™s') || pergunta.includes('mes')) {
  periodo = 'este m√™s';
}

console.log('[CODE] Pergunta:', pergunta);
console.log('[CODE] Per√≠odo detectado:', periodo);

// Chamar Gateway usando $http do n8n
const response = await $http.request({
  method: 'POST',
  url: 'https://gestorconecta-mcp-gateway.oxlser.easypanel.host/mcp/crm',
  headers: {
    'Content-Type': 'application/json',
    'X-API-Key': 'camaleao-mcp-e6b9c9c7e8ef5dbdfc8e80331eac4c0c254fbf6ccd7065c4af3af05a8bdeed60'
  },
  body: {
    _endpoint: 'espelho_bancario',
    periodo: periodo
  },
  json: true
});

console.log('[CODE] Resposta do Gateway:', response);

// Retornar mensagem formatada
return {
  json: {
    resposta: response.data,
    periodo: periodo,
    timestamp: new Date().toISOString()
  }
};
